def get_most_popular_animal(tweet, animals):

    mentions = {animal:0 for animal in animals}
    for token in tweet.split():
        if token in mentions:
            mentions[token] += 1

    most_mentioned = sorted(mentions, key=lambda x: mentions[x])[-1]
    return most_mentioned

